import json
import re
from datetime import datetime

def normalize_profile(profile):
    """Normalizes a professional profile according to the guidelines."""

    normalization_notes = []
    normalized_profile = profile.copy()

    # 1. Company Names
    def normalize_company_name(company_name):
        if not company_name:
            return company_name

        company_name = " ".join(company_name.split())  # Remove duplicate spaces
        company_name = re.sub(r'\.+', '.', company_name)  # Standardize periods

        # Apply standard capitalization (proper case for most names)
        words = company_name.split()
        normalized_words = []
        for word in words:
            if word.isupper() and word not in ["ACME"]:  # Preserve well-known acronyms (example ACME)
                normalized_words.append(word)
            else:
                normalized_words.append(word.capitalize())
        company_name = " ".join(normalized_words)

        # Standardize common variations
        company_name = company_name.replace("Inc.", "Incorporated")
        company_name = company_name.replace("Corp.", "Corporation")
        company_name = company_name.replace("Llc.", "LLC")
        company_name = company_name.replace("L.l.c.", "LLC")
        company_name = company_name.replace("Ltd.", "Limited")
        company_name = company_name.replace("Co.", "Company")
        company_name = company_name.replace("Self Employed", "Self-Employed")
        company_name = company_name.replace("Fleishmanhillard", "Fleishman-Hillard")

        return company_name

    # 2. Job Titles
    def normalize_job_title(job_title):
        if not job_title:
            return job_title

        job_title = job_title.replace("Sr.", "Senior")
        job_title = job_title.replace("Mgr", "Manager")
        job_title = job_title.replace("Proj.", "Project")
        job_title = job_title.replace("Mktg", "Marketing")

        # Standardize common role titles
        if "Manager, Marketing" in job_title:
            job_title = "Marketing Manager"

        return job_title

    # 3. Skills
    def normalize_skill(skill):
        if not skill:
            return skill

        skill = skill.strip()
        if skill == "Salesforce.com":
            skill = "Salesforce"
        elif skill == "Digital Mktg":
            skill = "Digital Marketing"
        elif skill == "Class Management":
            skill = "Classroom Management"

        return skill

    # 4. Locations
    def normalize_location(location):
        if not location:
            return location

        location = location.strip()
        if location == "NYC, New York":
            location = "New York, New York, United States"
            if "Standardized NYC to full city name" not in normalization_notes:
                normalization_notes.append("Standardized NYC to full city name")
        elif location == "Dallas, TX":
            location = "Dallas, Texas, United States"
        elif location == "Austin, Texas Area":
            location = "Austin, Texas, United States"
        return location

    # 5. Dates
    def normalize_date(date_string):
        if not date_string:
            return date_string

        date_string = date_string.strip()
        if date_string.lower() == "present":
            return "Present"

        try:
            # Attempt to parse various date formats
            date_obj = None
            for fmt in ("%B %Y", "%b %Y", "%m/%Y", "%Y-%m-%d", "%Y-%m"):
                try:
                    date_obj = datetime.strptime(date_string, fmt)
                    break
                except ValueError:
                    pass

            if date_obj:
                return date_obj.strftime("%Y-%m")
            else:
                return date_string  # Return original if parsing fails
        except ValueError:
            return date_string

    # Normalize top-level fields
    if "title" in normalized_profile:
        normalized_profile["title"] = normalize_job_title(normalized_profile["title"])

    if "location" in normalized_profile:
        normalized_profile["location"] = normalize_location(normalized_profile["location"])

    if "key_skills" in normalized_profile:
        normalized_profile["key_skills"] = [normalize_skill(skill) for skill in normalized_profile["key_skills"]]

    # Normalize experience entries
    if "experience" in normalized_profile:
        for exp in normalized_profile["experience"]:
            if "company" in exp:
                exp["company"] = normalize_company_name(exp["company"])
            if "title" in exp:
                exp["title"] = normalize_job_title(exp["title"])
            if "location" in exp:
                exp["location"] = normalize_location(exp["location"])
            if "duration" in exp:
                if "start_date" in exp["duration"]:
                    exp["duration"]["start_date"] = normalize_date(exp["duration"]["start_date"])
                if "end_date" in exp["duration"]:
                    exp["duration"]["end_date"] = normalize_date(exp["duration"]["end_date"])

    # Add normalization notes if any changes were made
    if normalization_notes:
        normalized_profile["normalization_notes"] = "; ".join(normalization_notes)

    return normalized_profile
