import json
import re
from datetime import datetime

def normalize_profile(profile):
    """Normalizes a professional profile according to the specified guidelines."""

    normalization_notes = []
    normalized_profile = profile.copy()

    # 1. Company Names
    def normalize_company_name(company_name):
        if not company_name:
            return company_name

        company_name = " ".join(company_name.split())  # Remove duplicate spaces
        company_name = re.sub(r'\s+', ' ', company_name).strip() # Remove extra spaces

        # Standard capitalization (proper case)
        company_name = ' '.join([word.capitalize() if word.lower() != 'inc' else 'Inc' for word in company_name.split()])

        # Specific replacements
        company_name = company_name.replace("Inc.", "Inc").replace("Incorporated", "Inc")
        company_name = company_name.replace("Lume Studios", "LUME Studios")
        company_name = company_name.replace("Self Employed", "Self-Employed")
        company_name = company_name.replace("Fleishmanhillard", "Fleishman-Hillard")

        return company_name

    # 2. Job Titles
    def normalize_job_title(job_title):
        if not job_title:
            return job_title

        job_title = job_title.replace("Sr.", "Senior").replace("Mgr", "Manager").replace("Proj.", "Project")
        return job_title

    # 3. Skills
    def normalize_skill(skill):
        if not skill:
            return skill

        skill = skill.replace("Salesforce.com", "Salesforce")
        skill = skill.replace("Digital Mktg", "Digital Marketing")
        skill = skill.replace("Class Management", "Classroom Management")

        return skill

    # 4. Locations
    def normalize_location(location):
        if not location:
            return location

        location = location.replace("NYC, New York", "New York, New York, United States")
        location = location.replace("Dallas, TX", "Dallas, Texas, United States")
        location = location.replace("Austin, Texas Area", "Austin, Texas, United States")

        return location

    # 5. Dates
    def normalize_date(date_string):
        if not date_string:
            return date_string

        date_string = date_string.strip()
        if date_string.lower() == "present":
            return "Present"

        try:
            # Attempt to parse various date formats
            date_object = None
            for fmt in ("%B %Y", "%b %Y", "%m/%Y", "%Y-%m-%d", "%Y-%m"):
                try:
                    date_object = datetime.strptime(date_string, fmt)
                    break
                except ValueError:
                    pass

            if date_object:
                return date_object.strftime("%Y-%m")

        except ValueError:
            # If parsing fails, return the original string
            return date_string

    # Normalize top-level title and location
    if "title" in normalized_profile:
        normalized_profile["title"] = normalize_job_title(normalized_profile["title"])
    if "location" in normalized_profile:
        normalized_profile["location"] = normalize_location(normalized_profile["location"])

    # Normalize key skills
    if "key_skills" in normalized_profile:
        normalized_profile["key_skills"] = [normalize_skill(skill) for skill in normalized_profile["key_skills"]]

    # Normalize experience
    if "experience" in normalized_profile:
        for experience in normalized_profile["experience"]:
            if "company" in experience:
                original_company = experience["company"]
                experience["company"] = normalize_company_name(experience["company"])
                if original_company != experience["company"]:
                    normalization_notes.append(f"Company name normalized from '{original_company}' to '{experience['company']}'")
            if "title" in experience:
                experience["title"] = normalize_job_title(experience["title"])
            if "location" in experience:
                experience["location"] = normalize_location(experience["location"])
            if "duration" in experience:
                if "start_date" in experience["duration"]:
                    original_start_date = experience["duration"]["start_date"]
                    experience["duration"]["start_date"] = normalize_date(experience["duration"]["start_date"])
                    if original_start_date != experience["duration"]["start_date"]:
                        normalization_notes.append(f"Start date normalized from '{original_start_date}' to '{experience['duration']['start_date']}'")
                if "end_date" in experience["duration"]:
                    original_end_date = experience["duration"]["end_date"]
                    experience["duration"]["end_date"] = normalize_date(experience["duration"]["end_date"])
                    if original_end_date != experience["duration"]["end_date"]:
                        normalization_notes.append(f"End date normalized from '{original_end_date}' to '{experience['duration']['end_date']}'")

    # Add normalization notes if any changes were made
    if normalization_notes:
        normalized_profile["normalization_notes"] = "; ".join(normalization_notes)

    return normalized_profile


# Example usage with the provided input profile:
input_profile = {
  "name": "John Smith",
  "title": "Sr. Marketing Mgr",
  "location": "NYC, New York",
  "key_skills": [
    "Digital Mktg",
    "Salesforce.com",
    "Content Strategy"
  ],
  "experience": [
    {
      "company": "ACME corp.",
      "title": "Senior Mktg Manager",
      "duration": {
        "start_date": "January 2020",
        "end_date": "Present",
        "date_range": "3 years 2 months"
      },
      "location": "New York City"
    }
  ]
}

normalized_profile = normalize_profile(input_profile)
print(json.dumps(normalized_profile, indent=2))
