import json
import re
from datetime import datetime

def normalize_profile(profile):
    """Normalizes a professional profile according to the guidelines."""

    normalization_notes = []
    normalized_profile = profile.copy()

    # 1. Company Names
    def normalize_company_name(company_name):
        if not company_name:
            return company_name

        company_name = " ".join(company_name.split())  # Remove duplicate spaces
        company_name = re.sub(r'\.+', '.', company_name)  # Standardize periods

        # Apply standard capitalization (proper case, preserve acronyms)
        words = company_name.split()
        normalized_words = []
        for word in words:
            if word.isupper() and len(word) > 1:  # Preserve acronyms
                normalized_words.append(word)
            else:
                normalized_words.append(word.capitalize())
        company_name = " ".join(normalized_words)

        # Standardize common variations
        company_name = company_name.replace("Inc.", "Incorporated")
        company_name = company_name.replace("Corp.", "Corporation")
        company_name = company_name.replace("Llc.", "LLC")
        company_name = company_name.replace("L.l.c.", "LLC")
        company_name = company_name.replace("Self Employed", "Self-Employed")
        company_name = company_name.replace("Fleishmanhillard", "Fleishman-Hillard")

        return company_name

    # 2. Job Titles
    def normalize_job_title(job_title):
        if not job_title:
            return job_title

        job_title = job_title.replace("Sr.", "Senior")
        job_title = job_title.replace("Mgr", "Manager")
        job_title = job_title.replace("Proj.", "Project")
        job_title = job_title.replace("Mktg", "Marketing")
        job_title = job_title.replace("Assoc.", "Associate")

        # Standardize "Manager, Marketing" to "Marketing Manager"
        if ", " in job_title:
            parts = job_title.split(", ")
            if len(parts) == 2 and parts[0] == "Manager":
                job_title = parts[1] + " Manager"

        return job_title

    # 3. Skills
    def normalize_skill(skill):
        if not skill:
            return skill

        skill = skill.strip()
        if skill.lower() == "salesforce.com":
            skill = "Salesforce"
        elif skill.lower() == "digital mktg":
            skill = "Digital Marketing"
        elif skill.lower() == "class management":
            skill = "Classroom Management"

        return skill

    # 4. Locations
    def normalize_location(location):
        if not location:
            return location

        location = location.replace("NYC", "New York")
        location = location.replace("New York City", "New York")

        if "," in location:
            parts = [s.strip() for s in location.split(",")]
            if len(parts) == 2:
                city, state = parts
                if state == "TX":
                    location = f"{city}, Texas, United States"
                elif state == "New York":
                    location = f"{city}, New York, United States"
                else:
                    location = f"{city}, {state}, United States"
            elif len(parts) == 3:
                # Assuming City, State, Country
                return ", ".join(parts)
            else:
                location = location + ", United States"
        elif "Area" in location:
            location = location.replace(" Area", ", United States")
        else:
            location = location + ", United States"

        return location

    # 5. Dates
    def normalize_date(date_string):
        if not date_string:
            return date_string

        date_string = date_string.strip()
        if date_string.lower() == "present":
            return "Present"

        try:
            # Attempt to parse various date formats
            date_obj = None
            for fmt in ("%B %Y", "%b %Y", "%m/%Y", "%Y-%m-%d", "%Y-%m"):
                try:
                    date_obj = datetime.strptime(date_string, fmt)
                    break
                except ValueError:
                    pass

            if date_obj:
                return date_obj.strftime("%Y-%m")
            else:
                # If parsing fails, return the original string
                return date_string
        except ValueError:
            return date_string

    # Normalize top-level fields
    if "title" in normalized_profile:
        original_title = normalized_profile["title"]
        normalized_profile["title"] = normalize_job_title(normalized_profile["title"])
        if original_title != normalized_profile["title"]:
            normalization_notes.append(f"Standardized job title from '{original_title}' to '{normalized_profile['title']}'")

    if "location" in normalized_profile:
        original_location = normalized_profile["location"]
        normalized_profile["location"] = normalize_location(normalized_profile["location"])
        if original_location != normalized_profile["location"]:
            normalization_notes.append(f"Standardized location from '{original_location}' to '{normalized_profile['location']}'")

    if "key_skills" in normalized_profile:
        original_skills = normalized_profile["key_skills"]
        normalized_profile["key_skills"] = [normalize_skill(skill) for skill in normalized_profile["key_skills"]]
        if original_skills != normalized_profile["key_skills"]:
            normalization_notes.append(f"Standardized skills from '{original_skills}' to '{normalized_profile['key_skills']}'")

    # Normalize experience entries
    if "experience" in normalized_profile:
        for exp in normalized_profile["experience"]:
            if "company" in exp:
                original_company = exp["company"]
                exp["company"] = normalize_company_name(exp["company"])
                if original_company != exp["company"]:
                    normalization_notes.append(f"Standardized company name from '{original_company}' to '{exp['company']}'")

            if "title" in exp:
                original_title = exp["title"]
                exp["title"] = normalize_job_title(exp["title"])
                if original_title != exp["title"]:
                    normalization_notes.append(f"Standardized job title from '{original_title}' to '{exp['title']}'")

            if "location" in exp:
                original_location = exp["location"]
                exp["location"] = normalize_location(exp["location"])
                if original_location != exp["location"]:
                    normalization_notes.append(f"Standardized location from '{original_location}' to '{exp['location']}'")

            if "duration" in exp:
                if "start_date" in exp["duration"]:
                    original_start_date = exp["duration"]["start_date"]
                    exp["duration"]["start_date"] = normalize_date(exp["duration"]["start_date"])
                    if original_start_date != exp["duration"]["start_date"]:
                        normalization_notes.append(f"Standardized start date from '{original_start_date}' to '{exp['duration']['start_date']}'")

                if "end_date" in exp["duration"]:
                    original_end_date = exp["duration"]["end_date"]
                    exp["duration"]["end_date"] = normalize_date(exp["duration"]["end_date"])
                    if original_end_date != exp["duration"]["end_date"]:
                        normalization_notes.append(f"Standardized end date from '{original_end_date}' to '{exp['duration']['end_date']}'")

    if normalization_notes:
        normalized_profile["normalization_notes"] = "; ".join(normalization_notes)

    return normalized_profile
