import json
import re
from datetime import datetime

def normalize_profile(profile):
    """Normalizes a professional profile according to the specified guidelines."""

    normalization_notes = []
    normalized_profile = profile.copy()

    # 1. Company Names
    def normalize_company_name(company_name):
        if not company_name:
            return company_name

        company_name = " ".join(company_name.split())  # Remove duplicate spaces
        company_name = re.sub(r'\s+', ' ', company_name).strip() # Remove extra spaces
        company_name = company_name.replace('&amp;', '&') # Handle HTML entities

        # Standardize capitalization (proper case, preserve acronyms)
        words = company_name.split()
        normalized_words = []
        for word in words:
            if word.isupper() and len(word) > 1:
                normalized_words.append(word)  # Preserve acronyms
            else:
                normalized_words.append(word.capitalize())
        company_name = " ".join(normalized_words)

        # Standardize common variations
        company_name = company_name.replace("Inc.", "Inc").replace("Incorporated", "Inc")
        company_name = company_name.replace("Llc", "LLC").replace("Ltd.", "Ltd")
        company_name = company_name.replace("Self Employed", "Self-Employed")
        company_name = company_name.replace("Fleishmanhillard", "Fleishman-Hillard")
        return company_name

    # 2. Job Titles
    def normalize_job_title(job_title):
        if not job_title:
            return job_title

        job_title = job_title.replace("Sr.", "Senior").replace("Sr", "Senior")
        job_title = job_title.replace("Mgr", "Manager").replace("Proj.", "Project")
        job_title = job_title.replace("Mktg", "Marketing")
        job_title = job_title.replace("Assoc.", "Associate")

        # Standardize capitalization (proper case)
        words = job_title.split()
        normalized_words = []
        for word in words:
            normalized_words.append(word.capitalize())
        job_title = " ".join(normalized_words)

        return job_title

    # 3. Skills
    def normalize_skill(skill):
        if not skill:
            return skill

        skill = skill.strip()
        skill = skill.replace("Salesforce.com", "Salesforce")
        skill = skill.replace("Class Management", "Classroom Management")

        # Standardize capitalization (proper case)
        skill = skill.capitalize()
        return skill

    # 4. Locations
    def normalize_location(location):
        if not location:
            return location

        location = location.strip()
        location = location.replace("NYC", "New York")
        location = location.replace("Austin, Texas Area", "Austin, Texas, United States")
        location = location.replace("Dallas, TX", "Dallas, Texas, United States")

        if "," in location:
            parts = [s.strip() for s in location.split(",")]
            if len(parts) == 2:
                city, state = parts
                if state == "NY":
                    location = f"{city}, New York, United States"
                elif state == "TX":
                    location = f"{city}, Texas, United States"
                else:
                    location = f"{city}, {state}, United States"
            elif len(parts) == 3:
                # Assuming City, State/Province, Country
                pass # Already in desired format
        else:
            # Attempt to add ", United States" if it seems like a US city
            if location in ["New York", "Los Angeles", "Chicago", "Houston", "Phoenix", "Philadelphia", "San Antonio", "San Diego", "Dallas", "San Jose"]:
                location = f"{location}, United States"

        return location

    # 5. Dates
    def normalize_date(date_string):
        if not date_string:
            return date_string

        date_string = date_string.strip()
        if date_string.lower() == "present":
            return "Present"

        try:
            # Attempt to parse various date formats
            date_object = None
            for fmt in ("%B %Y", "%b %Y", "%m/%Y", "%Y-%m-%d", "%Y-%m"):
                try:
                    date_object = datetime.strptime(date_string, fmt)
                    break
                except ValueError:
                    pass

            if date_object:
                return date_object.strftime("%Y-%m")
            else:
                return date_string  # Return original if parsing fails
        except ValueError:
            return date_string

    # Normalize top-level title and location
    if "title" in normalized_profile:
        original_title = normalized_profile["title"]
        normalized_profile["title"] = normalize_job_title(normalized_profile["title"])
        if original_title != normalized_profile["title"]:
            normalization_notes.append(f"Standardized job title from '{original_title}' to '{normalized_profile['title']}'")

    if "location" in normalized_profile:
        original_location = normalized_profile["location"]
        normalized_profile["location"] = normalize_location(normalized_profile["location"])
        if original_location != normalized_profile["location"]:
            normalization_notes.append(f"Standardized location from '{original_location}' to '{normalized_profile['location']}'")

    # Normalize key skills
    if "key_skills" in normalized_profile:
        original_skills = normalized_profile["key_skills"]
        normalized_profile["key_skills"] = [normalize_skill(skill) for skill in normalized_profile["key_skills"]]
        if original_skills != normalized_profile["key_skills"]:
            normalization_notes.append(f"Standardized key skills")

    # Normalize experience
    if "experience" in normalized_profile:
        for experience in normalized_profile["experience"]:
            if "company" in experience:
                original_company = experience["company"]
                experience["company"] = normalize_company_name(experience["company"])
                if original_company != experience["company"]:
                    normalization_notes.append(f"Standardized company name from '{original_company}' to '{experience['company']}'")

            if "title" in experience:
                original_title = experience["title"]
                experience["title"] = normalize_job_title(experience["title"])
                if original_title != experience["title"]:
                    normalization_notes.append(f"Standardized job title from '{original_title}' to '{experience['title']}' in experience")

            if "location" in experience:
                original_location = experience["location"]
                experience["location"] = normalize_location(experience["location"])
                if original_location != experience["location"]:
                    normalization_notes.append(f"Standardized location from '{original_location}' to '{experience['location']}' in experience")

            if "duration" in experience:
                if "start_date" in experience["duration"]:
                    original_start_date = experience["duration"]["start_date"]
                    experience["duration"]["start_date"] = normalize_date(experience["duration"]["start_date"])
                    if original_start_date != experience["duration"]["start_date"]:
                        normalization_notes.append(f"Standardized start date from '{original_start_date}' to '{experience['duration']['start_date']}' in experience")

                if "end_date" in experience["duration"]:
                    original_end_date = experience["duration"]["end_date"]
                    experience["duration"]["end_date"] = normalize_date(experience["duration"]["end_date"])
                    if original_end_date != experience["duration"]["end_date"]:
                        normalization_notes.append(f"Standardized end date from '{original_end_date}' to '{experience['duration']['end_date']}' in experience")

    if normalization_notes:
        normalized_profile["normalization_notes"] = "; ".join(normalization_notes)

    return normalized_profile
