import json
import re
from datetime import datetime

def normalize_profile(profile):
    """Normalizes a professional profile according to the specified guidelines."""

    normalization_notes = []
    normalized_profile = profile.copy()

    # 1. Company Names
    def normalize_company_name(company_name):
        if not company_name:
            return company_name

        company_name = " ".join(company_name.split())  # Remove duplicate spaces
        company_name = re.sub(r'\.+', '.', company_name)  # Standardize periods

        if company_name.upper() == company_name and company_name.isalpha():
            # Preserve all-caps acronyms
            pass
        else:
            company_name = company_name.title()

        company_name = company_name.replace("Inc.", "Incorporated")
        company_name = company_name.replace("Corp.", "Corp")
        company_name = company_name.replace("Llc.", "LLC")
        company_name = company_name.replace("L.L.C.", "LLC")
        company_name = company_name.replace("L. L. C.", "LLC")
        company_name = company_name.replace(" And ", " and ")
        company_name = company_name.replace(" Of ", " of ")
        company_name = company_name.replace("The ", "")
        company_name = company_name.strip()

        return company_name

    # 2. Job Titles
    def normalize_job_title(job_title):
        if not job_title:
            return job_title

        job_title = job_title.replace("Sr.", "Senior")
        job_title = job_title.replace("Mgr", "Manager")
        job_title = job_title.replace("Proj.", "Project")
        job_title = job_title.replace("Mktg", "Marketing")

        # Standardize order (e.g., "Manager, Marketing" -> "Marketing Manager")
        if ", " in job_title:
            parts = job_title.split(", ")
            if len(parts) == 2 and parts[0].lower() in ["manager", "director", "vp", "vice president", "president"]:
                job_title = parts[1] + " " + parts[0]

        return job_title

    # 3. Skills
    def normalize_skill(skill):
        if not skill:
            return skill

        skill = skill.strip()
        if skill.lower() == "salesforce.com":
            return "Salesforce"
        elif skill.lower() == "digital mktg":
            return "Digital Marketing"
        elif skill.lower() == "class management":
            return "Classroom Management"
        return skill

    # 4. Locations
    def normalize_location(location):
        if not location:
            return location

        location = location.replace("NYC", "New York")
        location = location.replace("New York City", "New York")

        if "," in location:
            parts = [s.strip() for s in location.split(",")]
            if len(parts) == 2:
                city, state = parts
                if state == "TX":
                    return f"{city}, Texas, United States"
                elif state == "New York":
                    return f"{city}, New York, United States"
                else:
                    return f"{city}, {state}, United States"
            elif len(parts) == 3:
                return ", ".join(parts)
        elif "Area" in location:
            location = location.replace(" Area", "")
            return f"{location}, United States"
        return location

    # 5. Dates
    def normalize_date(date_string):
        if not date_string:
            return date_string

        if date_string.lower() == "present":
            return "Present"

        try:
            # Attempt to parse various date formats
            date_object = None
            for fmt in ("%B %Y", "%b %Y", "%m/%Y", "%Y-%m-%d", "%Y-%m"):
                try:
                    date_object = datetime.strptime(date_string, fmt)
                    break
                except ValueError:
                    continue

            if date_object:
                return date_object.strftime("%Y-%m")
            else:
                return date_string  # Return original if parsing fails
        except ValueError:
            return date_string

    # Normalize top-level fields
    if "title" in normalized_profile:
        original_title = normalized_profile["title"]
        normalized_profile["title"] = normalize_job_title(normalized_profile["title"])
        if original_title != normalized_profile["title"]:
            normalization_notes.append(f"Standardized job title from '{original_title}' to '{normalized_profile['title']}'")

    if "location" in normalized_profile:
        original_location = normalized_profile["location"]
        normalized_profile["location"] = normalize_location(normalized_profile["location"])
        if original_location != normalized_profile["location"]:
            normalization_notes.append(f"Standardized location from '{original_location}' to '{normalized_profile['location']}'")

    if "key_skills" in normalized_profile:
        original_skills = normalized_profile["key_skills"]
        normalized_profile["key_skills"] = [normalize_skill(skill) for skill in normalized_profile["key_skills"]]
        if original_skills != normalized_profile["key_skills"]:
            normalization_notes.append(f"Standardized skills from '{original_skills}' to '{normalized_profile['key_skills']}'")

    # Normalize experience entries
    if "experience" in normalized_profile:
        for experience in normalized_profile["experience"]:
            if "company" in experience:
                original_company = experience["company"]
                experience["company"] = normalize_company_name(experience["company"])
                if original_company != experience["company"]:
                    normalization_notes.append(f"Standardized company name from '{original_company}' to '{experience['company']}'")

            if "title" in experience:
                original_exp_title = experience["title"]
                experience["title"] = normalize_job_title(experience["title"])
                if original_exp_title != experience["title"]:
                    normalization_notes.append(f"Standardized job title in experience from '{original_exp_title}' to '{experience['title']}'")

            if "location" in experience:
                original_exp_location = experience["location"]
                experience["location"] = normalize_location(experience["location"])
                if original_exp_location != experience["location"]:
                    normalization_notes.append(f"Standardized location in experience from '{original_exp_location}' to '{experience['location']}'")

            if "duration" in experience:
                if "start_date" in experience["duration"]:
                    original_start_date = experience["duration"]["start_date"]
                    experience["duration"]["start_date"] = normalize_date(experience["duration"]["start_date"])
                    if original_start_date != experience["duration"]["start_date"]:
                        normalization_notes.append(f"Standardized start date in experience from '{original_start_date}' to '{experience['duration']['start_date']}'")

                if "end_date" in experience["duration"]:
                    original_end_date = experience["duration"]["end_date"]
                    experience["duration"]["end_date"] = normalize_date(experience["duration"]["end_date"])
                    if original_end_date != experience["duration"]["end_date"]:
                        normalization_notes.append(f"Standardized end date in experience from '{original_end_date}' to '{experience['duration']['end_date']}'")

    # Add normalization notes if any changes were made
    if normalization_notes:
        normalized_profile["normalization_notes"] = "; ".join(normalization_notes)

    return normalized_profile
