import json
import re
from datetime import datetime

def normalize_profile(profile):
    """Normalizes a professional profile according to the specified guidelines."""

    normalization_notes = []
    normalized_profile = profile.copy()

    # 1. Company Names
    def normalize_company_name(company_name):
        if not company_name:
            return company_name

        company_name = " ".join(company_name.split())  # Remove duplicate spaces
        company_name = re.sub(r'\.+', '.', company_name)  # Standardize periods

        if company_name.upper() == company_name and company_name not in ["ACME", "LUME"]:
            company_name = company_name.title()
        else:
            company_name = company_name.title()

        company_name = company_name.replace("Inc.", "Incorporated")
        company_name = company_name.replace("Corp.", "Corporation")
        company_name = company_name.replace("Llc", "LLC")
        company_name = company_name.replace("Llp", "LLP")
        company_name = company_name.replace("Ltd.", "Limited")
        company_name = company_name.replace("Fleishmanhillard", "Fleishman-Hillard")

        return company_name

    # 2. Job Titles
    def normalize_job_title(job_title):
        if not job_title:
            return job_title

        job_title = job_title.replace("Sr.", "Senior")
        job_title = job_title.replace("Mgr", "Manager")
        job_title = job_title.replace("Proj.", "Project")
        job_title = job_title.replace("Mktg", "Marketing")

        # Standardize order (e.g., "Manager, Marketing" -> "Marketing Manager")
        if ", " in job_title:
            parts = job_title.split(", ")
            if len(parts) == 2:
                job_title = f"{parts[1]} {parts[0]}"

        return job_title

    # 3. Skills
    def normalize_skill(skill):
        if not skill:
            return skill

        skill = skill.strip()
        if skill.lower() == "salesforce.com":
            skill = "Salesforce"
        elif skill.lower() == "digital mktg":
            skill = "Digital Marketing"
        elif skill.lower() == "class management":
            skill = "Classroom Management"

        return skill

    # 4. Locations
    def normalize_location(location):
        if not location:
            return location

        location = location.replace("NYC", "New York")
        if ", TX" in location:
            location = location.replace(", TX", ", Texas, United States")
        if "Austin, Texas Area" in location:
            location = "Austin, Texas, United States"
        if "New York, New York, United States" in location:
            location = "New York, New York, United States"
        if "," in location and len(location.split(',')) == 2:
            parts = [p.strip() for p in location.split(',')]
            location = f"{parts[0]}, {parts[1]}, United States"

        return location

    # 5. Dates
    def normalize_date(date_string):
        if not date_string:
            return date_string

        date_string = date_string.strip()
        if date_string.lower() == "present":
            return "Present"

        try:
            # Attempt to parse various date formats
            date_object = None
            for fmt in ("%B %Y", "%b %Y", "%m/%Y", "%Y-%m-%d", "%Y-%m"):
                try:
                    date_object = datetime.strptime(date_string, fmt)
                    break
                except ValueError:
                    pass

            if date_object:
                return date_object.strftime("%Y-%m")
            else:
                return date_string  # Return original if parsing fails
        except ValueError:
            return date_string

    # Normalize top-level fields
    normalized_profile["title"] = normalize_job_title(normalized_profile["title"])
    normalized_profile["location"] = normalize_location(normalized_profile["location"])

    # Normalize key skills
    normalized_profile["key_skills"] = [normalize_skill(skill) for skill in normalized_profile["key_skills"]]

    # Normalize experience
    for experience in normalized_profile["experience"]:
        experience["company"] = normalize_company_name(experience["company"])
        experience["title"] = normalize_job_title(experience["title"])
        experience["location"] = normalize_location(experience["location"])

        # Normalize dates within experience
        experience["duration"]["start_date"] = normalize_date(experience["duration"]["start_date"])
        experience["duration"]["end_date"] = normalize_date(experience["duration"]["end_date"])

    # Collect normalization notes
    if profile["title"] != normalized_profile["title"]:
        normalization_notes.append(f"Standardized job title from '{profile['title']}' to '{normalized_profile['title']}'")
    if profile["location"] != normalized_profile["location"]:
        normalization_notes.append(f"Standardized location from '{profile['location']}' to '{normalized_profile['location']}'")
    if any(s1 != s2 for s1, s2 in zip(profile["key_skills"], normalized_profile["key_skills"])):
        normalization_notes.append("Standardized key skills")
    for i, (exp_orig, exp_norm) in enumerate(zip(profile["experience"], normalized_profile["experience"])):
        if exp_orig["company"] != exp_norm["company"]:
            normalization_notes.append(f"Standardized company name in experience {i+1} from '{exp_orig['company']}' to '{exp_norm['company']}'")
        if exp_orig["title"] != exp_norm["title"]:
            normalization_notes.append(f"Standardized job title in experience {i+1} from '{exp_orig['title']}' to '{exp_norm['title']}'")
        if exp_orig["location"] != exp_norm["location"]:
            normalization_notes.append(f"Standardized location in experience {i+1} from '{exp_orig['location']}' to '{exp_norm['location']}'")
        if exp_orig["duration"]["start_date"] != exp_norm["duration"]["start_date"]:
            normalization_notes.append(f"Standardized start date in experience {i+1} from '{exp_orig['duration']['start_date']}' to '{exp_norm['duration']['start_date']}'")
        if exp_orig["duration"]["end_date"] != exp_norm["duration"]["end_date"]:
            normalization_notes.append(f"Standardized end date in experience {i+1} from '{exp_orig['duration']['end_date']}' to '{exp_norm['duration']['end_date']}'")

    if normalization_notes:
        normalized_profile["normalization_notes"] = "; ".join(normalization_notes)

    return normalized_profile
