import json
import re
from datetime import datetime

def normalize_profile(profile):
    """Normalizes a professional profile according to the guidelines."""

    normalization_notes = []
    normalized_profile = profile.copy()

    # 1. Company Names
    def normalize_company_name(company_name):
        if not company_name:
            return company_name

        company_name = " ".join(company_name.split())  # Remove duplicate spaces
        company_name = re.sub(r'\.+', '.', company_name)  # Standardize periods
        company_name = company_name.replace('â€™', "'") # Standardize apostrophes

        # Apply capitalization and common replacements
        if company_name.upper() == company_name and len(company_name.split()) == 1:
            pass # Assume acronym if all caps and single word
        else:
            company_name = company_name.title()

        company_name = company_name.replace("Inc.", "Inc.")
        company_name = company_name.replace("Corp.", "Corp.")
        company_name = company_name.replace("Llc", "LLC")
        company_name = company_name.replace("Llp", "LLP")
        company_name = company_name.replace("Self Employed", "Self-Employed")
        company_name = company_name.replace("Fleishmanhillard", "Fleishman-Hillard")

        return company_name

    # 2. Job Titles
    def normalize_job_title(job_title):
        if not job_title:
            return job_title

        job_title = job_title.replace("Sr.", "Senior")
        job_title = job_title.replace("Mgr", "Manager")
        job_title = job_title.replace("Proj.", "Project")
        return job_title

    # 3. Skills
    def normalize_skill(skill):
        if not skill:
            return skill

        skill = skill.replace("Salesforce.com", "Salesforce")
        skill = skill.replace("Digital Mktg", "Digital Marketing")
        skill = skill.replace("Class Management", "Classroom Management")
        return skill

    # 4. Locations
    def normalize_location(location):
        if not location:
            return location

        location = location.replace("NYC, New York", "New York, New York, United States")
        location = location.replace("Dallas, TX", "Dallas, Texas, United States")
        location = location.replace("Austin, Texas Area", "Austin, Texas, United States")
        return location

    # 5. Dates
    def normalize_date(date_string):
        if not date_string:
            return date_string

        date_string = date_string.replace("Jan", "January")
        date_string = date_string.replace("Feb", "February")
        date_string = date_string.replace("Mar", "March")
        date_string = date_string.replace("Apr", "April")
        date_string = date_string.replace("May", "May")
        date_string = date_string.replace("Jun", "June")
        date_string = date_string.replace("Jul", "July")
        date_string = date_string.replace("Aug", "August")
        date_string = date_string.replace("Sep", "September")
        date_string = date_string.replace("Oct", "October")
        date_string = date_string.replace("Nov", "November")
        date_string = date_string.replace("Dec", "December")

        if date_string.lower() == "present":
            return "Present"

        try:
            date_object = datetime.strptime(date_string, "%B %Y")
            return date_object.strftime("%Y-%m")
        except ValueError:
            pass

        try:
            date_object = datetime.strptime(date_string, "%m/%Y")
            return date_object.strftime("%Y-%m")
        except ValueError:
            pass

        return date_string

    # Normalize top-level fields
    if "title" in normalized_profile:
        original_title = normalized_profile["title"]
        normalized_profile["title"] = normalize_job_title(normalized_profile["title"])
        if original_title != normalized_profile["title"]:
            normalization_notes.append(f"Standardized job title: '{original_title}' to '{normalized_profile['title']}'")

    if "location" in normalized_profile:
        original_location = normalized_profile["location"]
        normalized_profile["location"] = normalize_location(normalized_profile["location"])
        if original_location != normalized_profile["location"]:
            normalization_notes.append(f"Standardized location: '{original_location}' to '{normalized_profile['location']}'")

    if "key_skills" in normalized_profile:
        for i, skill in enumerate(normalized_profile["key_skills"]):
            original_skill = skill
            normalized_profile["key_skills"][i] = normalize_skill(skill)
            if original_skill != normalized_profile["key_skills"][i]:
                normalization_notes.append(f"Standardized skill: '{original_skill}' to '{normalized_profile['key_skills'][i]}'")

    # Normalize experience entries
    if "experience" in normalized_profile:
        for exp in normalized_profile["experience"]:
            if "company" in exp:
                original_company = exp["company"]
                exp["company"] = normalize_company_name(exp["company"])
                if original_company != exp["company"]:
                    normalization_notes.append(f"Standardized company name: '{original_company}' to '{exp['company']}'")

            if "title" in exp:
                original_title = exp["title"]
                exp["title"] = normalize_job_title(exp["title"])
                if original_title != exp["title"]:
                    normalization_notes.append(f"Standardized job title: '{original_title}' to '{exp['title']}'")

            if "location" in exp:
                original_location = exp["location"]
                exp["location"] = normalize_location(exp["location"])
                if original_location != exp["location"]:
                    normalization_notes.append(f"Standardized location: '{original_location}' to '{exp['location']}'")

            if "duration" in exp:
                if "start_date" in exp["duration"]:
                    original_start_date = exp["duration"]["start_date"]
                    exp["duration"]["start_date"] = normalize_date(exp["duration"]["start_date"])
                    if original_start_date != exp["duration"]["start_date"]:
                        normalization_notes.append(f"Standardized start date: '{original_start_date}' to '{exp['duration']['start_date']}'")

                if "end_date" in exp["duration"]:
                    original_end_date = exp["duration"]["end_date"]
                    exp["duration"]["end_date"] = normalize_date(exp["duration"]["end_date"])
                    if original_end_date != exp["duration"]["end_date"]:
                        normalization_notes.append(f"Standardized end date: '{original_end_date}' to '{exp['duration']['end_date']}'")

    # Add normalization notes if any changes were made
    if normalization_notes:
        normalized_profile["normalization_notes"] = "; ".join(normalization_notes)

    return normalized_profile
