import json
import re
from datetime import datetime

def normalize_profile(profile):
    """
    Normalizes a professional profile according to the specified guidelines.

    Args:
        profile (dict): A dictionary representing the profile data.

    Returns:
        dict: A normalized version of the profile data.
    """

    normalization_notes = []
    normalized_profile = profile.copy()

    # 1. Company Names
    def normalize_company_name(company_name):
        if not company_name:
            return company_name

        company_name = " ".join(company_name.split())  # Remove duplicate spaces
        company_name = company_name.replace("â€™", "'")  # Standardize apostrophes

        # Apply standard capitalization (proper case, preserve acronyms)
        words = company_name.split()
        normalized_words = []
        for word in words:
            if word.isupper() and len(word) > 1:
                normalized_words.append(word)  # Preserve acronyms
            else:
                normalized_words.append(word.capitalize())
        company_name = " ".join(normalized_words)

        # Standardize common variations
        company_name = company_name.replace("Inc.", "Inc")
        company_name = company_name.replace("Incorporated", "Inc")
        company_name = company_name.replace("Corp.", "Corp")
        company_name = company_name.replace("Corporation", "Corp")
        company_name = company_name.replace("Llc", "LLC")
        company_name = company_name.replace("Ltd.", "Ltd")
        company_name = company_name.replace("Limited", "Ltd")
        company_name = company_name.replace("Self Employed", "Self-Employed")
        company_name = company_name.replace("Self employed", "Self-Employed")
        company_name = company_name.replace("Self-Employed", "Self-Employed") # Ensure consistency

        return company_name

    # 2. Job Titles
    def normalize_job_title(job_title):
        if not job_title:
            return job_title

        job_title = job_title.replace("Sr.", "Senior")
        job_title = job_title.replace("Mgr", "Manager")
        job_title = job_title.replace("Proj.", "Project")
        job_title = job_title.replace("Mktg", "Marketing")
        job_title = job_title.replace("Assoc.", "Associate")

        # Standardize "Manager, Marketing" to "Marketing Manager"
        if "Manager," in job_title:
            parts = job_title.split(",")
            if len(parts) == 2:
                job_title = parts[1].strip() + " " + parts[0].replace("Manager", "").strip() + " Manager"
                job_title = " ".join(job_title.split()) # Remove extra spaces

        return job_title

    # 3. Skills
    def normalize_skill(skill):
        if not skill:
            return skill

        skill = skill.replace("Salesforce.com", "Salesforce")
        skill = skill.replace("Class Management", "Classroom Management")
        skill = skill.replace("Digital Mktg", "Digital Marketing")
        return skill

    # 4. Locations
    def normalize_location(location):
        if not location:
            return location

        location = location.replace("NYC", "New York")
        location = location.replace("New York City", "New York")

        if ", TX" in location:
            location = location.replace(", TX", ", Texas, United States")
        if "Austin, Texas Area" in location:
            location = "Austin, Texas, United States"
        if "New York, New York, United States" in location:
            location = "New York, New York, United States"

        if "," not in location and "United States" not in location:
            location = location + ", United States"

        return location

    # 5. Dates
    def normalize_date(date_string):
        if not date_string:
            return date_string

        date_string = date_string.strip()
        if date_string.lower() == "present":
            return "Present"

        try:
            # Attempt to parse various date formats
            date_obj = None
            for fmt in ("%B %Y", "%b %Y", "%m/%Y", "%Y-%m-%d", "%Y-%m"):
                try:
                    date_obj = datetime.strptime(date_string, fmt)
                    break
                except ValueError:
                    pass

            if date_obj:
                return date_obj.strftime("%Y-%m")  # Standardize to YYYY-MM
            else:
                return date_string  # Return original if parsing fails
        except ValueError:
            return date_string

    # Normalize top-level fields
    normalized_profile["title"] = normalize_job_title(normalized_profile.get("title", ""))
    normalized_profile["location"] = normalize_location(normalized_profile.get("location", ""))

    # Normalize key skills
    if "key_skills" in normalized_profile:
        normalized_profile["key_skills"] = [normalize_skill(skill) for skill in normalized_profile["key_skills"]]

    # Normalize experience
    if "experience" in normalized_profile:
        for exp in normalized_profile["experience"]:
            if "company" in exp:
                exp["company"] = normalize_company_name(exp["company"])
            if "title" in exp:
                exp["title"] = normalize_job_title(exp["title"])
            if "location" in exp:
                exp["location"] = normalize_location(exp["location"])
            if "duration" in exp:
                if "start_date" in exp["duration"]:
                    exp["duration"]["start_date"] = normalize_date(exp["duration"]["start_date"])
                if "end_date" in exp["duration"]:
                    exp["duration"]["end_date"] = normalize_date(exp["duration"]["end_date"])

    # Collect normalization notes
    if profile["title"] != normalized_profile["title"]:
        normalization_notes.append(f"Standardized job title from '{profile['title']}' to '{normalized_profile['title']}'")
    if profile["location"] != normalized_profile["location"]:
        normalization_notes.append(f"Standardized location from '{profile['location']}' to '{normalized_profile['location']}'")
    if "key_skills" in profile and profile["key_skills"] != normalized_profile["key_skills"]:
        normalization_notes.append(f"Standardized skills from '{profile['key_skills']}' to '{normalized_profile['key_skills']}'")

    if "experience" in profile:
        for i, exp in enumerate(profile["experience"]):
            if "company" in exp and exp["company"] != normalized_profile["experience"][i]["company"]:
                normalization_notes.append(f"Standardized company name from '{exp['company']}' to '{normalized_profile['experience'][i]['company']}' in experience {i+1}")
            if "title" in exp and exp["title"] != normalized_profile["experience"][i]["title"]:
                normalization_notes.append(f"Standardized job title from '{exp['title']}' to '{normalized_profile['experience'][i]['title']}' in experience {i+1}")
            if "location" in exp and exp["location"] != normalized_profile["experience"][i]["location"]:
                normalization_notes.append(f"Standardized location from '{exp['location']}' to '{normalized_profile['experience'][i]['location']}' in experience {i+1}")
            if "duration" in exp:
                if "start_date" in exp["duration"] and exp["duration"]["start_date"] != normalized_profile["experience"][i]["duration"]["start_date"]:
                    normalization_notes.append(f"Standardized start date from '{exp['duration']['start_date']}' to '{normalized_profile['experience'][i]['duration']['start_date']}' in experience {i+1}")
                if "end_date" in exp["duration"] and exp["duration"]["end_date"] != normalized_profile["experience"][i]["duration"]["end_date"]:
                    normalization_notes.append(f"Standardized end date from '{exp['duration']['end_date']}' to '{normalized_profile['experience'][i]['duration']['end_date']}' in experience {i+1}")

    if normalization_notes:
        normalized_profile["normalization_notes"] = "; ".join(normalization_notes)

    return normalized_profile
