import json
import re
from datetime import datetime

def normalize_profile(profile):
    """Normalizes a professional profile according to the guidelines."""

    normalization_notes = []
    normalized_profile = profile.copy()

    # 1. Company Names
    def normalize_company_name(company_name):
        if not company_name:
            return company_name

        company_name = " ".join(company_name.split())  # Remove duplicate spaces
        company_name = re.sub(r'\s+', ' ', company_name).strip()  # Remove extra spaces

        # Standardize capitalization
        if company_name.upper() != company_name and company_name.lower() != company_name:
            company_name = company_name.title()

        # Specific replacements
        company_name = company_name.replace("Inc.", "Incorporated")
        company_name = company_name.replace("Corp.", "Corp")
        company_name = company_name.replace("Llc.", "LLC")
        company_name = company_name.replace("L.L.C.", "LLC")
        company_name = company_name.replace("Fleishmanhillard", "Fleishman-Hillard")
        company_name = company_name.replace("Self Employed", "Self-Employed")

        return company_name

    # 2. Job Titles
    def normalize_job_title(job_title):
        if not job_title:
            return job_title

        job_title = job_title.replace("Sr.", "Senior")
        job_title = job_title.replace("Mgr", "Manager")
        job_title = job_title.replace("Proj.", "Project")
        job_title = job_title.replace("Mktg", "Marketing")

        return job_title

    # 3. Skills
    def normalize_skill(skill):
        if not skill:
            return skill

        skill = skill.replace("Salesforce.com", "Salesforce")
        skill = skill.replace("Class Management", "Classroom Management")

        # Add more skill normalization rules as needed
        return skill

    # 4. Locations
    def normalize_location(location):
        if not location:
            return location

        location = location.replace("NYC, New York", "New York, New York, United States")
        location = location.replace("Dallas, TX", "Dallas, Texas, United States")
        location = location.replace("Austin, Texas Area", "Austin, Texas, United States")

        return location

    # 5. Dates
    def normalize_date(date_string):
        if not date_string:
            return date_string

        date_string = date_string.replace("Jan", "January")
        date_string = date_string.replace("Feb", "February")
        date_string = date_string.replace("Mar", "March")
        date_string = date_string.replace("Apr", "April")
        date_string = date_string.replace("May", "May")
        date_string = date_string.replace("Jun", "June")
        date_string = date_string.replace("Jul", "July")
        date_string = date_string.replace("Aug", "August")
        date_string = date_string.replace("Sep", "September")
        date_string = date_string.replace("Oct", "October")
        date_string = date_string.replace("Nov", "November")
        date_string = date_string.replace("Dec", "December")

        if date_string.lower() == "present":
            return "Present"

        try:
            date_object = datetime.strptime(date_string, "%B %Y")
            return date_object.strftime("%Y-%m")
        except ValueError:
            pass

        try:
            date_object = datetime.strptime(date_string, "%m/%Y")
            return date_object.strftime("%Y-%m")
        except ValueError:
            pass

        # If all parsing fails, return the original string
        return date_string

    # Normalize top-level fields
    normalized_profile["title"] = normalize_job_title(normalized_profile.get("title", ""))
    normalized_profile["location"] = normalize_location(normalized_profile.get("location", ""))

    # Normalize key skills
    if "key_skills" in normalized_profile and isinstance(normalized_profile["key_skills"], list):
        normalized_profile["key_skills"] = [normalize_skill(skill) for skill in normalized_profile["key_skills"]]

    # Normalize experience
    if "experience" in normalized_profile and isinstance(normalized_profile["experience"], list):
        for experience in normalized_profile["experience"]:
            if "company" in experience:
                experience["company"] = normalize_company_name(experience["company"])
            if "title" in experience:
                experience["title"] = normalize_job_title(experience["title"])
            if "location" in experience:
                experience["location"] = normalize_location(experience["location"])

            if "duration" in experience:
                if "start_date" in experience["duration"]:
                    experience["duration"]["start_date"] = normalize_date(experience["duration"]["start_date"])
                if "end_date" in experience["duration"]:
                    experience["duration"]["end_date"] = normalize_date(experience["duration"]["end_date"])

    # Collect normalization notes
    if profile["title"] != normalized_profile["title"]:
        normalization_notes.append(f"Standardized job title: '{profile['title']}' to '{normalized_profile['title']}'")
    if profile["location"] != normalized_profile["location"]:
        normalization_notes.append(f"Standardized location: '{profile['location']}' to '{normalized_profile['location']}'")
    if "key_skills" in profile and "key_skills" in normalized_profile and profile["key_skills"] != normalized_profile["key_skills"]:
        normalization_notes.append(f"Standardized skills: '{profile['key_skills']}' to '{normalized_profile['key_skills']}'")

    for i in range(len(profile.get("experience", []))):
        if profile["experience"][i].get("company") != normalized_profile["experience"][i].get("company"):
            normalization_notes.append(f"Standardized company name: '{profile['experience'][i].get('company')}' to '{normalized_profile['experience'][i].get('company')}'")
        if profile["experience"][i].get("title") != normalized_profile["experience"][i].get("title"):
            normalization_notes.append(f"Standardized job title: '{profile['experience'][i].get('title')}' to '{normalized_profile['experience'][i].get('title')}'")
        if profile["experience"][i].get("location") != normalized_profile["experience"][i].get("location"):
            normalization_notes.append(f"Standardized location: '{profile['experience'][i].get('location')}' to '{normalized_profile['experience'][i].get('location')}'")
        if profile["experience"][i].get("duration", {}).get("start_date") != normalized_profile["experience"][i].get("duration", {}).get("start_date"):
            normalization_notes.append(f"Standardized start date: '{profile['experience'][i].get('duration', {}).get('start_date')}' to '{normalized_profile['experience'][i].get('duration', {}).get('start_date')}'")
        if profile["experience"][i].get("duration", {}).get("end_date") != normalized_profile["experience"][i].get("duration", {}).get("end_date"):
            normalization_notes.append(f"Standardized end date: '{profile['experience'][i].get('duration', {}).get('end_date')}' to '{normalized_profile['experience'][i].get('duration', {}).get('end_date')}'")

    if normalization_notes:
        normalized_profile["normalization_notes"] = "; ".join(normalization_notes)

    return normalized_profile
