import json
import re
from datetime import datetime

def normalize_profile(profile):
    """Normalizes a professional profile according to the specified guidelines."""

    normalization_notes = []
    normalized_profile = profile.copy()

    # 1. Company Names
    def normalize_company_name(company_name):
        if not company_name:
            return company_name

        company_name = " ".join(company_name.split())  # Remove duplicate spaces
        company_name = re.sub(r'\s+', ' ', company_name).strip() # Remove extra spaces

        # Standard capitalization (proper case, preserve acronyms)
        if company_name.isupper() and company_name not in ["ACME", "LUME"]:
            normalized_name = company_name # Keep it all caps if it's an acronym
        else:
            normalized_name = ' '.join([word.capitalize() for word in company_name.split()])

        # Standardize common variations
        normalized_name = normalized_name.replace("Inc.", "Inc").replace("Incorporated", "Inc")
        normalized_name = normalized_name.replace("Llc", "LLC").replace("Ltd", "Ltd.")
        normalized_name = normalized_name.replace("Self Employed", "Self-Employed")
        normalized_name = normalized_name.replace("Fleishmanhillard", "Fleishman-Hillard")

        return normalized_name

    # 2. Job Titles
    def normalize_job_title(job_title):
        if not job_title:
            return job_title

        job_title = job_title.replace("Sr.", "Senior").replace("Sr", "Senior")
        job_title = job_title.replace("Mgr", "Manager").replace("Mktg", "Marketing")
        job_title = job_title.replace("Proj.", "Project")
        job_title = job_title.replace("Assoc.", "Associate")

        # Standardize department and function terms (e.g., "Marketing", "Engineering")
        # Add more as needed based on common patterns
        return job_title

    # 3. Skills
    def normalize_skill(skill):
        if not skill:
            return skill

        skill = skill.strip()
        if skill.lower() == "salesforce.com":
            return "Salesforce"
        elif skill.lower() == "digital mktg":
            return "Digital Marketing"
        elif skill.lower() == "class management":
            return "Classroom Management"
        # Add more skill normalization rules as needed
        return skill

    # 4. Locations
    def normalize_location(location):
        if not location:
            return location

        location = location.strip()
        if location.lower() == "nyc, new york":
            location = "New York, New York, United States"
            if "Standardized NYC to full city name" not in normalization_notes:
                normalization_notes.append("Standardized NYC to full city name")
        elif location.lower() == "dallas, tx":
            location = "Dallas, Texas, United States"
        elif "austin, texas area" in location.lower():
            location = "Austin, Texas, United States"
        # Add more location normalization rules as needed
        return location

    # 5. Dates
    def normalize_date(date_string):
        if not date_string:
            return date_string

        date_string = date_string.strip()
        if date_string.lower() == "present":
            return "Present"

        try:
            # Attempt to parse various date formats
            date_obj = None
            for fmt in ("%B %Y", "%b %Y", "%m/%Y", "%Y-%m-%d", "%Y-%m"):
                try:
                    date_obj = datetime.strptime(date_string, fmt)
                    break
                except ValueError:
                    pass

            if date_obj:
                return date_obj.strftime("%Y-%m")  # Standardize to YYYY-MM
            else:
                return date_string # Return original if parsing fails
        except ValueError:
            return date_string

    # Normalize top-level fields
    if "title" in normalized_profile:
        normalized_profile["title"] = normalize_job_title(normalized_profile["title"])
    if "location" in normalized_profile:
        normalized_profile["location"] = normalize_location(normalized_profile["location"])
    if "key_skills" in normalized_profile:
        normalized_profile["key_skills"] = [normalize_skill(skill) for skill in normalized_profile["key_skills"]]

    # Normalize experience entries
    if "experience" in normalized_profile:
        for experience in normalized_profile["experience"]:
            if "company" in experience:
                experience["company"] = normalize_company_name(experience["company"])
            if "title" in experience:
                experience["title"] = normalize_job_title(experience["title"])
            if "location" in experience:
                experience["location"] = normalize_location(experience["location"])
            if "duration" in experience:
                if "start_date" in experience["duration"]:
                    experience["duration"]["start_date"] = normalize_date(experience["duration"]["start_date"])
                if "end_date" in experience["duration"]:
                    experience["duration"]["end_date"] = normalize_date(experience["duration"]["end_date"])

    # Add normalization notes if any changes were made
    if normalization_notes:
        normalized_profile["normalization_notes"] = "; ".join(normalization_notes)

    return normalized_profile
