import json
import re
from datetime import datetime

def normalize_profile(profile):
    """Normalizes a professional profile according to the guidelines."""

    normalization_notes = []
    normalized_profile = profile.copy()

    # 1. Company Names
    def normalize_company_name(company_name):
        if not company_name:
            return company_name

        company_name = " ".join(company_name.split())  # Remove duplicate spaces
        company_name = re.sub(r'\s+', ' ', company_name).strip()  # Standardize spaces

        # Standardize capitalization (proper case, but preserve acronyms)
        if company_name.isupper():
            # Check for common acronyms that should remain uppercase
            if company_name not in ["ACME", "IBM", "CEO", "CFO", "CTO", "COO", "AI", "ML", "AWS", "GCP", "SaaS", "B2B", "B2C"]:
                company_name = company_name.title()
        else:
            company_name = company_name.title()

        # Specific replacements
        company_name = company_name.replace("Inc.", "Incorporated")
        company_name = company_name.replace("Corp.", "Corp")
        company_name = company_name.replace("Llc", "LLC")
        company_name = company_name.replace("Llp", "LLP")
        company_name = company_name.replace("Ltd.", "Ltd")
        company_name = company_name.replace("Self Employed", "Self-Employed")
        company_name = company_name.replace("Fleishmanhillard", "Fleishman-Hillard")

        return company_name

    # 2. Job Titles
    def normalize_job_title(job_title):
        if not job_title:
            return job_title

        job_title = job_title.replace("Sr.", "Senior")
        job_title = job_title.replace("Mgr", "Manager")
        job_title = job_title.replace("Proj.", "Project")
        job_title = job_title.replace("Mktg", "Marketing")

        # Standardize capitalization
        job_title = job_title.title()

        return job_title

    # 3. Skills
    def normalize_skill(skill):
        if not skill:
            return skill

        skill = skill.strip()

        if skill == "Salesforce.com":
            skill = "Salesforce"
        elif skill in ["Digital Mktg", "Digital Marketing"]:
            skill = "Digital Marketing"
        elif skill in ["Class Management", "Classroom Management"]:
            skill = "Classroom Management"
        return skill

    # 4. Locations
    def normalize_location(location):
        if not location:
            return location

        location = location.replace("NYC", "New York")

        if location == "Dallas, TX":
            location = "Dallas, Texas, United States"
        elif "Austin, Texas Area" in location:
            location = "Austin, Texas, United States"
        elif location == "New York, New York, United States":
            location = "New York, New York, United States"
        elif location == "New York City":
            location = "New York, New York, United States"
        return location

    # 5. Dates
    def normalize_date(date_string):
        if not date_string:
            return date_string

        date_string = date_string.strip()

        if date_string.lower() == "present":
            return "Present"

        try:
            # Attempt to parse various date formats
            date_obj = None
            for fmt in ("%B %Y", "%b %Y", "%m/%Y", "%Y-%m-%d", "%Y-%m"):
                try:
                    date_obj = datetime.strptime(date_string, fmt)
                    break
                except ValueError:
                    pass

            if date_obj:
                return date_obj.strftime("%Y-%m")  # Standardize to YYYY-MM
            else:
                # If parsing fails, return the original string
                return date_string
        except ValueError:
            return date_string

    # Normalize top-level fields
    normalized_profile["title"] = normalize_job_title(normalized_profile.get("title", ""))
    normalized_profile["location"] = normalize_location(normalized_profile.get("location", ""))

    # Normalize skills
    if "key_skills" in normalized_profile and isinstance(normalized_profile["key_skills"], list):
        normalized_profile["key_skills"] = [normalize_skill(skill) for skill in normalized_profile["key_skills"]]

    # Normalize experience
    if "experience" in normalized_profile and isinstance(normalized_profile["experience"], list):
        for exp in normalized_profile["experience"]:
            if "company" in exp:
                exp["company"] = normalize_company_name(exp["company"])
            if "title" in exp:
                exp["title"] = normalize_job_title(exp["title"])
            if "location" in exp:
                exp["location"] = normalize_location(exp["location"])

            if "duration" in exp:
                if "start_date" in exp["duration"]:
                    exp["duration"]["start_date"] = normalize_date(exp["duration"]["start_date"])
                if "end_date" in exp["duration"]:
                    exp["duration"]["end_date"] = normalize_date(exp["duration"]["end_date"])

    # Collect normalization notes
    if profile["title"] != normalized_profile["title"]:
        normalization_notes.append(f"Standardized job title: '{profile['title']}' to '{normalized_profile['title']}'")
    if profile["location"] != normalized_profile["location"]:
        normalization_notes.append(f"Standardized location: '{profile['location']}' to '{normalized_profile['location']}'")
    if "key_skills" in profile and "key_skills" in normalized_profile:
        for i in range(min(len(profile["key_skills"]), len(normalized_profile["key_skills"]))):
            if profile["key_skills"][i] != normalized_profile["key_skills"][i]:
                normalization_notes.append(f"Standardized skill: '{profile['key_skills'][i]}' to '{normalized_profile['key_skills'][i]}'")
    if "experience" in profile and "experience" in normalized_profile:
        for i in range(len(profile["experience"])):
            if "company" in profile["experience"][i] and "company" in normalized_profile["experience"][i]:
                if profile["experience"][i]["company"] != normalized_profile["experience"][i]["company"]:
                    normalization_notes.append(f"Standardized company name: '{profile['experience'][i]['company']}' to '{normalized_profile['experience'][i]['company']}'")
            if "duration" in profile["experience"][i] and "duration" in normalized_profile["experience"][i]:
                if "start_date" in profile["experience"][i]["duration"] and "start_date" in normalized_profile["experience"][i]["duration"]:
                    if profile["experience"][i]["duration"]["start_date"] != normalized_profile["experience"][i]["duration"]["start_date"]:
                        normalization_notes.append(f"Standardized start date: '{profile['experience'][i]['duration']['start_date']}' to '{normalized_profile['experience'][i]['duration']['start_date']}'")
                if "end_date" in profile["experience"][i]["duration"] and "end_date" in normalized_profile["experience"][i]["duration"]:
                    if profile["experience"][i]["duration"]["end_date"] != normalized_profile["experience"][i]["duration"]["end_date"]:
                        normalization_notes.append(f"Standardized end date: '{profile['experience'][i]['duration']['end_date']}' to '{normalized_profile['experience'][i]['duration']['end_date']}'")

    if normalization_notes:
        normalized_profile["normalization_notes"] = "; ".join(normalization_notes)

    return normalized_profile
