import json
import re
from datetime import datetime

def normalize_profile(profile):
    """
    Normalizes a professional profile according to the specified guidelines.

    Args:
        profile (dict): A dictionary representing the profile data.

    Returns:
        dict: A normalized version of the profile data.
    """

    normalization_notes = []
    normalized_profile = profile.copy()

    # 1. Company Names
    def normalize_company_name(company_name):
        if not company_name:
            return company_name

        company_name = " ".join(company_name.split())  # Remove duplicate spaces
        company_name = company_name.replace("â€™", "'")  # Standardize apostrophes

        # Standardize capitalization (proper case, preserve acronyms)
        words = company_name.split()
        normalized_words = []
        for word in words:
            if word.isupper() and len(word) > 1:
                normalized_words.append(word)  # Preserve acronyms
            else:
                normalized_words.append(word.capitalize())
        company_name = " ".join(normalized_words)

        # Standardize common variations
        company_name = company_name.replace("Inc.", "Inc").replace("Incorporated", "Inc")
        company_name = company_name.replace("Corp.", "Corp").replace("Corporation", "Corp")
        company_name = company_name.replace("Llc.", "LLC").replace("Limited Liability Company", "LLC")
        company_name = company_name.replace("Ltd.", "Ltd").replace("Limited", "Ltd")
        company_name = company_name.replace("Self Employed", "Self-Employed")
        company_name = company_name.replace("Fleishmanhillard", "Fleishman-Hillard")

        return company_name

    # 2. Job Titles
    def normalize_job_title(job_title):
        if not job_title:
            return job_title

        job_title = job_title.replace("Sr.", "Senior").replace("Sr", "Senior")
        job_title = job_title.replace("Mgr", "Manager").replace("Mktg", "Marketing")
        job_title = job_title.replace("Proj.", "Project")
        job_title = job_title.replace("Assoc.", "Associate")

        # Standardize common role titles
        if "Marketing Manager" in job_title or "Manager, Marketing" in job_title:
            job_title = "Marketing Manager"

        return job_title

    # 3. Skills
    def normalize_skill(skill):
        if not skill:
            return skill

        skill = skill.replace("Salesforce.com", "Salesforce")
        skill = skill.replace("Digital Mktg", "Digital Marketing")
        skill = skill.replace("Class Management", "Classroom Management")

        return skill

    # 4. Locations
    def normalize_location(location):
        if not location:
            return location

        location = location.replace("NYC", "New York")
        location = location.replace("New York City", "New York")
        location = location.replace("Austin, Texas Area", "Austin, Texas, United States")
        location = location.replace("Dallas, TX", "Dallas, Texas, United States")

        if "," in location:
            parts = location.split(",")
            city = parts[0].strip()
            state_country = parts[1].strip() if len(parts) > 1 else ""

            if state_country == "New York":
                return "New York, New York, United States"
            elif state_country == "Texas":
                return f"{city}, Texas, United States"
            elif state_country == "CA":
                return f"{city}, California, United States"
            else:
                return location
        else:
            return location

    # 5. Dates
    def normalize_date(date_string):
        if not date_string:
            return date_string

        date_string = date_string.replace("Jan", "January").replace("Feb", "February").replace("Mar", "March").replace("Apr", "April").replace("May", "May").replace("Jun", "June").replace("Jul", "July").replace("Aug", "August").replace("Sep", "September").replace("Oct", "October").replace("Nov", "November").replace("Dec", "December")

        if date_string.lower() == "present":
            return "Present"

        try:
            date_object = datetime.strptime(date_string, "%B %Y")
            return date_object.strftime("%Y-%m")
        except ValueError:
            try:
                date_object = datetime.strptime(date_string, "%m/%Y")
                return date_object.strftime("%Y-%m")
            except ValueError:
                return date_string  # Return original if parsing fails

    # Normalize top-level fields
    normalized_profile["title"] = normalize_job_title(normalized_profile.get("title", ""))
    normalized_profile["location"] = normalize_location(normalized_profile.get("location", ""))

    # Normalize key skills
    if "key_skills" in normalized_profile and isinstance(normalized_profile["key_skills"], list):
        normalized_profile["key_skills"] = [normalize_skill(skill) for skill in normalized_profile["key_skills"]]

    # Normalize experience
    if "experience" in normalized_profile and isinstance(normalized_profile["experience"], list):
        for experience in normalized_profile["experience"]:
            if "company" in experience:
                experience["company"] = normalize_company_name(experience["company"])
            if "title" in experience:
                experience["title"] = normalize_job_title(experience["title"])
            if "location" in experience:
                experience["location"] = normalize_location(experience["location"])
            if "duration" in experience:
                if "start_date" in experience["duration"]:
                    experience["duration"]["start_date"] = normalize_date(experience["duration"]["start_date"])
                if "end_date" in experience["duration"]:
                    experience["duration"]["end_date"] = normalize_date(experience["duration"]["end_date"])

    # Collect normalization notes
    if profile["title"] != normalized_profile["title"]:
        normalization_notes.append(f"Standardized job title: '{profile['title']}' to '{normalized_profile['title']}'")
    if profile["location"] != normalized_profile["location"]:
        normalization_notes.append(f"Standardized location: '{profile['location']}' to '{normalized_profile['location']}'")
    if "key_skills" in profile and "key_skills" in normalized_profile and profile["key_skills"] != normalized_profile["key_skills"]:
        normalization_notes.append(f"Standardized skills: '{profile['key_skills']}' to '{normalized_profile['key_skills']}'")
    if "experience" in profile and "experience" in normalized_profile:
        for i in range(len(profile["experience"])):
            if profile["experience"][i]["company"] != normalized_profile["experience"][i]["company"]:
                normalization_notes.append(f"Standardized company name: '{profile['experience'][i]['company']}' to '{normalized_profile['experience'][i]['company']}'")
            if profile["experience"][i]["title"] != normalized_profile["experience"][i]["title"]:
                normalization_notes.append(f"Standardized job title in experience: '{profile['experience'][i]['title']}' to '{normalized_profile['experience'][i]['title']}'")
            if profile["experience"][i]["location"] != normalized_profile["experience"][i]["location"]:
                normalization_notes.append(f"Standardized location in experience: '{profile['experience'][i]['location']}' to '{normalized_profile['experience'][i]['location']}'")
            if profile["experience"][i]["duration"]["start_date"] != normalized_profile["experience"][i]["duration"]["start_date"]:
                normalization_notes.append(f"Standardized start date in experience: '{profile['experience'][i]['duration']['start_date']}' to '{normalized_profile['experience'][i]['duration']['start_date']}'")
            if profile["experience"][i]["duration"]["end_date"] != normalized_profile["experience"][i]["duration"]["end_date"]:
                normalization_notes.append(f"Standardized end date in experience: '{profile['experience'][i]['duration']['end_date']}' to '{normalized_profile['experience'][i]['duration']['end_date']}'")

    if normalization_notes:
        normalized_profile["normalization_notes"] = "; ".join(normalization_notes)

    return normalized_profile
