import json
import re
from datetime import datetime

def normalize_profile(profile):
    """Normalizes a professional profile according to the guidelines."""

    normalization_notes = []
    normalized_profile = profile.copy()

    # 1. Company Names
    def normalize_company_name(company_name):
        if not company_name:
            return company_name

        company_name = " ".join(company_name.split())  # Remove duplicate spaces
        company_name = re.sub(r'\s+', ' ', company_name).strip()  # Remove extra spaces

        # Standardize capitalization
        if company_name.upper() != company_name and company_name.lower() != company_name:
            company_name = company_name.title()

        # Specific company name normalizations
        company_name = company_name.replace("Inc.", "Inc").replace("Incorporated", "Inc")
        company_name = company_name.replace("Corp.", "Corp").replace("Corporation", "Corp")
        company_name = company_name.replace("Llc.", "LLC").replace("Limited Liability Company", "LLC")
        company_name = company_name.replace("Fleishmanhillard", "Fleishman-Hillard")
        company_name = company_name.replace("Self Employed", "Self-Employed")
        company_name = company_name.replace("Self employed", "Self-Employed")
        company_name = company_name.replace("LUME studios", "LUME Studios")

        return company_name

    # 2. Job Titles
    def normalize_job_title(job_title):
        if not job_title:
            return job_title

        job_title = job_title.replace("Sr.", "Senior").replace("Sr", "Senior")
        job_title = job_title.replace("Mgr", "Manager").replace("Mktg", "Marketing")
        job_title = job_title.replace("Proj.", "Project")
        job_title = job_title.replace("Assoc.", "Associate")

        # Standardize capitalization (title case)
        job_title = job_title.title()

        return job_title

    # 3. Skills
    def normalize_skill(skill):
        if not skill:
            return skill

        skill = skill.strip()

        # Specific skill normalizations
        if "Salesforce.com" in skill:
            skill = "Salesforce"
        elif skill.lower() == "digital mktg":
            skill = "Digital Marketing"
        elif skill.lower() == "class management":
            skill = "Classroom Management"

        return skill

    # 4. Locations
    def normalize_location(location):
        if not location:
            return location

        location = location.replace("NYC", "New York")

        if ", TX" in location:
            location = location.replace(", TX", ", Texas, United States")
        if "Austin, Texas Area" in location:
            location = "Austin, Texas, United States"
        if "New York, New York, United States" in location:
            location = "New York, New York, United States"
        if "New York City" in location:
            location = "New York, New York, United States"

        return location

    # 5. Dates
    def normalize_date(date_string):
        if not date_string:
            return date_string

        date_string = date_string.strip()

        if date_string.lower() == "present":
            return "Present"

        try:
            # Attempt to parse various date formats
            date_obj = None
            for fmt in ("%B %Y", "%b %Y", "%m/%Y", "%Y-%m-%d", "%Y-%m"):
                try:
                    date_obj = datetime.strptime(date_string, fmt)
                    break
                except ValueError:
                    pass

            if date_obj:
                return date_obj.strftime("%Y-%m")
            else:
                # If parsing fails, return the original string
                return date_string
        except ValueError:
            return date_string

    # Normalize top-level fields
    normalized_profile["title"] = normalize_job_title(normalized_profile["title"])
    normalized_profile["location"] = normalize_location(normalized_profile["location"])
    normalized_profile["key_skills"] = [normalize_skill(skill) for skill in normalized_profile["key_skills"]]

    # Normalize experience entries
    for experience in normalized_profile["experience"]:
        experience["company"] = normalize_company_name(experience["company"])
        experience["title"] = normalize_job_title(experience["title"])
        experience["location"] = normalize_location(experience["location"])

        # Normalize dates within experience
        experience["duration"]["start_date"] = normalize_date(experience["duration"]["start_date"])
        experience["duration"]["end_date"] = normalize_date(experience["duration"]["end_date"])

    # Collect normalization notes
    if profile["title"] != normalized_profile["title"]:
        normalization_notes.append(f"Standardized job title: '{profile['title']}' to '{normalized_profile['title']}'")
    if profile["location"] != normalized_profile["location"]:
        normalization_notes.append(f"Standardized location: '{profile['location']}' to '{normalized_profile['location']}'")
    if profile["key_skills"] != normalized_profile["key_skills"]:
        normalization_notes.append(f"Standardized skills: '{profile['key_skills']}' to '{normalized_profile['key_skills']}'")
    for i, (original_exp, normalized_exp) in enumerate(zip(profile["experience"], normalized_profile["experience"])):
        if original_exp["company"] != normalized_exp["company"]:
            normalization_notes.append(f"Standardized company name in experience {i+1}: '{original_exp['company']}' to '{normalized_exp['company']}'")
        if original_exp["title"] != normalized_exp["title"]:
            normalization_notes.append(f"Standardized job title in experience {i+1}: '{original_exp['title']}' to '{normalized_exp['title']}'")
        if original_exp["location"] != normalized_exp["location"]:
            normalization_notes.append(f"Standardized location in experience {i+1}: '{original_exp['location']}' to '{normalized_exp['location']}'")
        if original_exp["duration"]["start_date"] != normalized_exp["duration"]["start_date"]:
            normalization_notes.append(f"Standardized start date in experience {i+1}: '{original_exp['duration']['start_date']}' to '{normalized_exp['duration']['start_date']}'")
        if original_exp["duration"]["end_date"] != normalized_exp["duration"]["end_date"]:
            normalization_notes.append(f"Standardized end date in experience {i+1}: '{original_exp['duration']['end_date']}' to '{normalized_exp['duration']['end_date']}'")

    if normalization_notes:
        normalized_profile["normalization_notes"] = "; ".join(normalization_notes)

    return normalized_profile
